	}
}

.scrollBanner {
	position: relative;
	padding-bottom: 26.25%;
	margin-bottom:50px;
}

.scrollBanner__image {
	width:$full; 
	height:$full;
	img {
		width:$full; 
		height:$full;
		@include position(absolute, $top:0);
	}
}

.afterImage {
	@include position(absolute, $top:0);
	transform: translate(100%, 0px);
	overflow: hidden;
	img {
		transform: translate(-100%, 0px);
	}
}


@media screen and (max-width: 1400px){
