			}
		}
	}

	.skillList {
		.title {
			@include margin($bottom:10px);
		}
	}
	.contactArea {
		.btnGroup {
			flex-wrap:wrap;
			a + a {
				@include margin($top:15px, $left:0);
			}
		}
	}
}

@media screen and (max-width: 640px){

	.sVisualWrap {
		height:200px;
		h3 {
			font-size:2.4em;
		}
	}
	.tab {
		div {
			a {
				@include padding($right:10px, $left:10px);
				height:45px;
				line-height:normal;
				@include center;
				word-break:keep-all;
				font-size:1em;}
		}
	} 
	.glider {
		height:45px;
	}

	.pjList {
		&.col3 {
			border-top:1px solid #555;
			li {
				width:$full;
				margin:0;
				border-bottom:1px solid #555;
			}
		}
		li {
			.imgArea {
				display:none;
				img {
					display:none;
				}
			}
			.txtArea {
				@include margin($top:15px, $bottom:15px);
				.title {
					@include margin($bottom:15px);
				}
			}
		}
	}

	
	.pjList {
		@mixin layout {
			display:block;
			width:80%;
			margin:0 auto;
		}
		li {
			&.pc_design, &.pc_etc {
				@include margin($top:15px);
				.imgArea {
					@include layout;
					img {
						@include layout;
					}
				}
				.txtArea {
					text-align:center;
					.row {
						&.f_start {
							justify-content:center;
							div {
								width:auto;
							}
						}
					}
				}
			}
		}
	}

	.introWrap {
		.introtext {
			.title {
				font-size:1.8em;
			}
			p {
				font-size:1.2em;
			}
		}
	}
}

